    def update_exif_overlay_position(self):
        """EXIF    (  )"""
        if not hasattr(self, 'exif_overlay'):
            return
            
        viewport_rect = self.viewport().rect()
        print(f"[DEBUG]  : {viewport_rect.width()} x {viewport_rect.height()}")
        
        #        
        min_width = 400
        min_height = 300
        max_width = max(min_width, int(viewport_rect.width() * 0.9))  # 90% 
        max_height = max(min_height, int(viewport_rect.height() * 0.9))  # 90% 
        
        #    
        overlay_width = min(700, max_width)  #  700px
        overlay_height = min(500, max_height)  #  500px
        
        #     
        if overlay_width > viewport_rect.width():
            overlay_width = viewport_rect.width() - 20  #  20px
        if overlay_height > viewport_rect.height():
            overlay_height = viewport_rect.height() - 20  #  20px
        
        #    ( )
        x = max(10, (viewport_rect.width() - overlay_width) // 2)
        y = max(10, (viewport_rect.height() - overlay_height) // 2)
        
        print(f"[DEBUG]  : {overlay_width} x {overlay_height}, : ({x}, {y})")
        self.exif_overlay.setGeometry(x, y, overlay_width, overlay_height)

    def show_exif_overlay(self, image_path):
        """EXIF  """
        print(f"[DEBUG] EXIF   : {image_path}")
        
        if not PIL_AVAILABLE:
            print("[DEBUG] PIL  ,   ")
            self.prompt_text_area.setPlainText("PIL(Pillow)    EXIF    .\n\npip install Pillow  .")
            self.paste_prompt_btn.setEnabled(False)
            self.update_exif_overlay_position()
            self.exif_overlay.setVisible(True)
            self.exif_overlay.raise_()
            return
            
        try:
            #  EXIF  
            prompt_info = self.extract_ai_prompt_from_image(image_path)
            
            if prompt_info:
                print(f"[DEBUG] AI   : {len(prompt_info)}")
                self.prompt_text_area.setPlainText(prompt_info)
                self.paste_prompt_btn.setEnabled(True)
            else:
                print("[DEBUG] AI   ,   ")
                #    
                basic_info = self.get_basic_image_info(image_path)
                self.prompt_text_area.setPlainText(f"  AI     .\n\n{basic_info}")
                self.paste_prompt_btn.setEnabled(False)
            
            print("[DEBUG] EXIF   ...")
            self.update_exif_overlay_position()
            self.exif_overlay.setVisible(True)
            self.exif_overlay.raise_()
            print("[DEBUG] EXIF   ")
            
        except Exception as e:
            print(f"[ERROR] EXIF   : {e}")
            import traceback
            traceback.print_exc()
            #    
            self.prompt_text_area.setPlainText(f"EXIF     :\n{str(e)}")
            self.paste_prompt_btn.setEnabled(False)
            self.update_exif_overlay_position()
            self.exif_overlay.setVisible(True)
            self.exif_overlay.raise_()

