    def handle_paste_shortcut(self):
        """Ctrl+V   -  """
        #            
        if (self.book_list.hasFocus() and 
            self.book_list.selectedItems() and 
            self.clipboard_pages):
            
            selected_book_item = self.book_list.selectedItems()[0]
