    def apply_custom_theme(self):
        """   -   """
        file_dialog = QFileDialog()
        file_path, _ = file_dialog.getOpenFileName(
            self,
            "  ",
            "",
            "  (*.png *.jpg *.jpeg *.bmp *.gif *.tiff *.tif *.webp)"
        )
        
        if file_path:
            #    
            from PySide6.QtWidgets import QMessageBox
            
            msg_box = QMessageBox(self)
            msg_box.setWindowTitle("  ")
            msg_box.setText("      .")
            msg_box.setInformativeText(" ?\n\n    .")
            msg_box.setIcon(QMessageBox.Question)
            
            restart_btn = msg_box.addButton("", QMessageBox.AcceptRole)
            cancel_btn = msg_box.addButton("", QMessageBox.RejectRole)
            
            msg_box.exec()
            
            if msg_box.clickedButton() == restart_btn:
                #    
                self.custom_background_image = file_path
                
                # UI    
                self.current_theme = " "
                self.save_ui_settings()
                
                #  
                import sys
                import os
                
                #   
                script_path = os.path.abspath(sys.argv[0])
                
                #    
                import subprocess
                subprocess.Popen([sys.executable, script_path])
                
                #   
                self.close()
                
            else:
                #     
                for action in self.theme_group.actions():
                    if action.text().endswith(self.current_theme):
                        action.setChecked(True)
                        break
        else:
            #       
            for action in self.theme_group.actions():
                if action.text().endswith(self.current_theme):
                    action.setChecked(True)
                    break
            
    def apply_background_image(self, image_path):
        """  ."""
        try:
            from PySide6.QtGui import QPixmap, QImageReader
            import os
            
            #     
            if not os.path.exists(image_path):
                print(f"[ERROR]    : {image_path}")
                self.handle_custom_theme_image_failure(image_path)
                return
            
            #    
            reader = QImageReader(image_path)
            reader.setAutoTransform(True)  # EXIF    
            reader.setDecideFormatFromContent(True)  #     
            reader.setQuality(100)  #   
            
            #   
            image = reader.read()
            if image.isNull():
                print(f"[ERROR]   : {image_path}")
                self.handle_custom_theme_image_failure(image_path)
                return
            
            #      
            pixmap = QPixmap.fromImage(image, Qt.PreferDither | Qt.AutoColor)
            if pixmap.isNull():
                print(f"[ERROR]   : {image_path}")
                self.handle_custom_theme_image_failure(image_path)
                return
            
            #   
            self.background_pixmap = pixmap
            
            #    
            self.apply_image_brightness()
            
            #    
            central_widget = self.centralWidget()
            if central_widget:
                central_widget.setAttribute(Qt.WA_TranslucentBackground, True)
                central_widget.setStyleSheet("background: transparent;")
            
            #     
            self.apply_custom_theme_transparency_new()
            
            #    (paintEvent )
            self.update()
            
        except Exception as e:
            print(f"[ERROR]    : {e}")
            self.handle_custom_theme_image_failure(image_path)

