    def cleanup_unused_images(self):
        """    ."""
        if send2trash is None:
            QMessageBox.warning(self, "", "send2trash   .\npip install send2trash  .")
            return
            
        # images      
        images_dir = get_images_directory()
        if not os.path.exists(images_dir):
            return
        
        #      
        used_images = set()
        for book_name, book_data in self.state.books.items():
            pages = book_data.get("pages", [])
            for page in pages:
                image_path = page.get("image_path", "")
                if image_path and os.path.exists(image_path):
                    #    
                    used_images.add(os.path.abspath(image_path))
        
        # images     
        image_extensions = {'.png', '.jpg', '.jpeg', '.bmp', '.gif', '.tiff', '.tif', '.webp'}
        all_images = []
        
        for root, dirs, files in os.walk(images_dir):
            for file in files:
                file_path = os.path.join(root, file)
                if os.path.splitext(file)[1].lower() in image_extensions:
                    all_images.append(os.path.abspath(file_path))
        
        #    
        unused_images = []
        for image_path in all_images:
            if image_path not in used_images:
                unused_images.append(image_path)
        
        if not unused_images:
            QMessageBox.information(self, " ", "   .")
            return
        
        #  
        count = len(unused_images)
        file_list = "\n".join([os.path.basename(path) for path in unused_images[:10]])
        if count > 10:
            file_list += f"\n...  {count - 10} "
            
        reply = QMessageBox.question(
            self,
            "  ",
            f"   {count}  ?\n\n"
            f" :\n{file_list}",
            QMessageBox.Yes | QMessageBox.No,
            QMessageBox.No
        )
        
        if reply == QMessageBox.Yes:
            success_count = 0
            failed_files = []
            
            for image_path in unused_images:
                try:
                    send2trash(image_path)
                    success_count += 1
                    print(f"[DEBUG]  : {image_path}")
                except Exception as e:
                    failed_files.append(os.path.basename(image_path))
                    print(f"[ERROR]   : {image_path} - {e}")
            
            #  
            if failed_files:
                QMessageBox.warning(
                    self,
                    "  ",
                    f"{success_count}   .\n"
                    f"  {len(failed_files)}:\n" + 
                    "\n".join(failed_files[:5]) + 
                    (f"\n...  {len(failed_files) - 5} " if len(failed_files) > 5 else "")
                )
            else:
                QMessageBox.information(
                    self,
                    " ",
                    f"{success_count}     ."
                )

    def cleanup_unused_images_silent(self):
        """      (  )"""
        if send2trash is None:
            return
            
        try:
            # images      
            images_dir = get_images_directory()
            if not os.path.exists(images_dir):
                return
            
            #      
            used_images = set()
            for book_name, book_data in self.state.books.items():
                pages = book_data.get("pages", [])
                for page in pages:
                    image_path = page.get("image_path", "")
                    if image_path and os.path.exists(image_path):
                        #    
                        used_images.add(os.path.abspath(image_path))
            
            # images     
            image_extensions = {'.png', '.jpg', '.jpeg', '.bmp', '.gif', '.tiff', '.tif', '.webp'}
            unused_images = []
            
            for root, dirs, files in os.walk(images_dir):
                for file in files:
                    file_path = os.path.join(root, file)
                    if os.path.splitext(file)[1].lower() in image_extensions:
                        abs_path = os.path.abspath(file_path)
                        if abs_path not in used_images:
                            unused_images.append(abs_path)
            
            #     
            for image_path in unused_images:
                try:
                    send2trash(image_path)
                    print(f"[DEBUG]  :   - {os.path.basename(image_path)}")
                except Exception as e:
                    print(f"[ERROR]   : {image_path} - {e}")
                    
        except Exception as e:
            print(f"[ERROR]     : {e}")
    
